import React, { useState } from 'react';
import './HotelCard.css'; // Import the CSS file

function HotelCard() {
  // Mock data for static display
  const hotel = {
    name: "Hampton Inn",
    rating: 3.8,
    location: "Downtown, City Center",
    price: 100,
    reviewCount: 234,
    images: [
      "https://media.istockphoto.com/id/119926339/photo/resort-swimming-pool.jpg?s=612x612&w=0&k=20&c=9QtwJC2boq3GFHaeDsKytF4-CavYKQuy1jBD2IRfYKc=", // These won't actually load
      "https://plus.unsplash.com/premium_photo-1661964071015-d97428970584?fm=jpg&q=60&w=3000&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MXx8aG90ZWx8ZW58MHx8MHx8fDA%3D", // Just placeholders for the static demo
    ],
  };
  
  const currency = "$";
  const exchangeRate = 1;
  const convertedPrice = (hotel.price * exchangeRate).toFixed(2);

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const nextImage = () => {
    setCurrentImageIndex((prev) => (prev + 1) % hotel.images.length);
  };

  const prevImage = () => {
    setCurrentImageIndex((prev) => (prev - 1 + hotel.images.length) % hotel.images.length);
  };

  return (
    <div className="hotel-card">
      {/* Image carousel */}
      <div className="image-container">
        {/* For static demo, use a placeholder color instead of actual images */}
        <div className="hotel-image-placeholder"></div>
        
        {/* Navigation arrows */}
        <button 
          onClick={prevImage}
          className="nav-button prev-button"
          aria-label="Previous image"
        >
          &lt;
        </button>
        <button 
          onClick={nextImage}
          className="nav-button next-button"
          aria-label="Next image"
        >
          &gt;
        </button>
        
        {/* Brand logo */}
        <div className="hotel-logo">
          <div className="logo-placeholder">Hampton Inn</div>
        </div>
      </div>
      
      {/* Hotel info */}
      <div className="hotel-info">
        <div className="info-container">
          <div className="hotel-details">
            <h2 className="hotel-name">Hampton Inn</h2>
            <div className="hotel-rating">
              <span className="stars">★★★★</span>
              <span className="rating-text">3.8 out of 5</span>
            </div>
            <p className="hotel-location">Downtown, City Center</p>
            <p className="review-count">Based on 234 reviews</p>
          </div>
          
          {/* Price section */}
          <div className="price-section">
            <p className="price-label">From</p>
            <p className="price-amount">$ 100.00</p>
            <button className="book-button">Book now</button>
          </div>
        </div>
        
        {/* Review section */}
        <div className="review-section">
          <button 
            className="review-link"
          >
            See all reviews →
          </button>
        </div>
      </div>
    </div>
  );
}

export default HotelCard;
