import React, { useState } from 'react';
import './HotelCard.css'; // Import the CSS file

function HotelCard({ hotel, currency, exchangeRate, onReviewClick }) {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const nextImage = () => {
    setCurrentImageIndex((prev) => (prev + 1) % hotel.images.length);
  };

  const prevImage = () => {
    setCurrentImageIndex((prev) => (prev - 1 + hotel.images.length) % hotel.images.length);
  };

  // Calculate price in selected currency
  const convertedPrice = (hotel.price * exchangeRate).toFixed(2);

  return (
    <div className="hotel-card">
      {/* Image carousel */}
      <div className="image-container">
        <img 
          src={hotel.images[currentImageIndex]} 
          alt={`${hotel.name} - view ${currentImageIndex + 1}`}
          className="hotel-image"
        />
        
        {/* Navigation arrows */}
        <button 
          onClick={prevImage}
          className="nav-button prev-button"
          aria-label="Previous image"
        >
          &lt;
        </button>
        <button 
          onClick={nextImage}
          className="nav-button next-button"
          aria-label="Next image"
        >
          &gt;
        </button>
        
        {/* Brand logo */}
        <div className="hotel-logo">
          <img src={hotel.logo} alt={`${hotel.name} logo`} />
        </div>
      </div>
      
      {/* Hotel info */}
      <div className="hotel-info">
        <div className="info-container">
          <div className="hotel-details">
            <h2 className="hotel-name">{hotel.name}</h2>
            <div className="hotel-rating">
              <span className="stars">★★★★</span>
              <span className="rating-text">{hotel.rating} out of 5</span>
            </div>
            <p className="hotel-location">{hotel.location}</p>
            <p className="review-count">Based on {hotel.reviewCount} reviews</p>
          </div>
          
          {/* Price section */}
          <div className="price-section">
            <p className="price-label">From</p>
            <p className="price-amount">{currency} {convertedPrice}</p>
            <button className="book-button">Book now</button>
          </div>
        </div>
        
        {/* Review section */}
        <div className="review-section">
          <button 
            onClick={onReviewClick}
            className="review-link"
          >
            See all reviews →
          </button>
        </div>
      </div>
    </div>
  );
}

export default HotelCard;
