import React, { useState } from 'react';
import './HotelCard.css';

function HotelCard({ hotel, currency, exchangeRate, onReviewClick }) {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const nextImage = () => setCurrentImageIndex((prev) => (prev + 1) % hotel.images.length);
  const prevImage = () => setCurrentImageIndex((prev) => (prev - 1 + hotel.images.length) % hotel.images.length);
  
  // Calculate price in selected currency
  const convertedPrice = (hotel.price * exchangeRate).toFixed(2);
  
  return (
    <div className="card">
      <div className="image-section">
        <img src={hotel.images[currentImageIndex]} alt={hotel.name} />
        <button onClick={prevImage} className="arrow left">&lt;</button>
        <button onClick={nextImage} className="arrow right">&gt;</button>
        <div className="logo"><img src={hotel.logo} alt="logo" /></div>
      </div>
      
      <div className="content">
        <div className="details">
          <h3>{hotel.name}</h3>
          <div className="rating">★★★★ {hotel.rating}/5</div>
          <p>{hotel.location}</p>
          <small>{hotel.reviewCount} reviews</small>
        </div>
        
        <div className="price">
          <div>From</div>
          <div className="amount">{currency} {(hotel.price * exchangeRate).toFixed(2)}</div>
          <button className="book">Book now</button>
        </div>
      </div>
      
      <div className="reviews">
        <button onClick={onReviewClick}>See all reviews →</button>
      </div>
    </div>
  );
}

export default HotelCard;
