import logo from "./logo.svg";
import "./App.css";
import { useState, useEffect } from "react";
import { mockExchangeRates } from './mockData/mockExchangeData';
import { mockHotels } from "./mockData/mockHotelData";
import HotelCard from "./components/HotelCard";

function App() {
  const [hotels, setHotels] = useState([]);
  const [exchangeRates, setExchangeRates] = useState({});
  const [sortBy, setSortBy] = useState('distance');
  const [currency, setCurrency] = useState('USD');
  const [selectedHotel, setSelectedHotel] = useState(null);

  useEffect(() => {
    // SUPPOSE TO FETCH THEIR API HERE  
    // fetch('http://wsXX.worldskills.org/webwanderer/module_d_api-cors-version.php/hotel.json')
    // .then(response => response.json())
    // .then(data => setHotels(data));

    // fetch('http://wsXX.worldskills.org/webwanderer/module_d_api-cors-version.php/exchange.json')
    //   .then(response => response.json())
    //   .then(data => setExchangeRates(data));

    setHotels(mockHotels);
    setExchangeRates(mockExchangeRates);
  }, []);


  return (
    <div className="container">
      {/* Dropdowns */}
      <div className="header">
        <div className="dropdownContainer">
          <label>Sort by</label>
          <select onChange={(e) => setSortBy(e.target.value)}>
            <option value="distance">Distance</option>
            <option value="distance">Amentities: low-high</option>
            <option value="distance">Amentities: high-low</option>
            <option value="distance">Price: high-low</option>
            <option value="distance">Price: high-low</option>
            <option value="price">Price</option>
          </select>
        </div>
        <div className="dropdownContainer">
          <label>Select currency</label>
          <select onChange={(e) => setCurrency(e.target.value)}>
            {Object.keys(exchangeRates).map((currencyCode) => (
              <option key={currencyCode} value={currencyCode}>
                {currencyCode}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Hotel Listings */}
      <div className="hotelSection">
        <h2>Hotels in Singapore</h2>
        <div className="hotelRows">
        {hotels.slice(0, 5).map(hotel => (
          <HotelCard 
            key={hotel.id} 
            hotel={hotel} 
            currency={currency} 
            exchangeRates={exchangeRates} 
            onReviewClick={() => setSelectedHotel(hotel)}
          />
        ))}
        </div>
      </div>

      {/* Reviews Popup */}
      {/* {selectedHotel && (
        <ReviewsPopup 
          hotel={selectedHotel} 
          onClose={() => setSelectedHotel(null)} 
        />
      )} */}
    </div>
  );
}

export default App;
